package entidade;

public class Departamentos {

	private int idDepartamntos;
	private String nome;
	
	public Departamentos() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public Departamentos(String nome) {
		super();
		this.nome = nome;
	}

	public int getIdDepartamntos() {
		return idDepartamntos;
	}

	public void setIdDepartamntos(int idDepartamntos) {
		this.idDepartamntos = idDepartamntos;
	}

	public String getNome() {
		return nome;
	}

	public void setNome(String nome) {
		this.nome = nome;
	}
	
	
	
}
